import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Upload.css";
import Header from "../components/Header";
import Footer from "../components/Footer";

const Upload = () => {
  const [bookImage, setBookImage] = useState(null);
  const [bookName, setBookName] = useState("");
  const [authorName, setAuthorName] = useState("");
  const [publisher, setPublisher] = useState("");
  const [genre, setGenre] = useState("소설");
  const [condition, setCondition] = useState("상태좋음");
  const [memo, setMemo] = useState("");

  const navigate = useNavigate();

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setBookImage(URL.createObjectURL(file));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const bookData = {
      bookImage,
      bookName,
      authorName,
      publisher,
      genre,
      condition,
      memo,
    };

    navigate("/author", { state: bookData }); // 데이터를 state로 전달하며 이동
  };

  return (
    <div>
      <Header />
      <h2>책 올리기</h2>
      <form onSubmit={handleSubmit}>
        {/* 폼 구성 */}
        <div style={{ textAlign: "center" }}>
          <button type="submit" style={{ padding: "10px 20px", fontSize: "16px", background: "#000", color: "#fff", border: "none", cursor: "pointer" }}>
            확인
          </button>
        </div>
      </form>
      <Footer />
    </div>
  );
};

export default Upload;
