import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Author.css";
import Header from "../components/Header";
import Footer from "../components/Footer";

const books = [
  { id: 1, title: "달까지 가자 완벽해결", author: "이이에", tag: "중고거래", image: "/img/book1.jpeg" },
  { id: 2, title: "아몬드", author: "손원평", tag: "중고거래", image: "/img/book2.jpeg" },
  { id: 3, title: "오늘밤 세계에서 이 사랑이 사라진다 해도", author: "이치조 미사키", tag: "중고거래", image: "/img/book3.jpeg" },
  { id: 4, title: "돌이킬 수 없는 약속", author: "아카가와 쥰", tag: "중고거래", image: "/img/book4.jpeg" },
  // 나머지 데이터도 개별 이미지 경로 추가
];

const Author = () => {
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState("");
  const [category, setCategory] = useState("모두");
  const [genre, setGenre] = useState("모두");
  const [sort, setSort] = useState("최신순");
  const itemsPerPage = 16;
  const navigate = useNavigate();

  // 필터 및 검색어 반영
  const filteredBooks = books
    .filter((book) =>
      book.title.toLowerCase().includes(searchTerm.toLowerCase())
    )
    .filter((book) => (category === "모두" ? true : book.tag === category));

  // 정렬 적용
  const sortedBooks = [...filteredBooks].sort((a, b) => {
    if (sort === "최신순") return b.id - a.id;
    if (sort === "낮은 가격순") return a.title.localeCompare(b.title);
    return 0; // 기본 최신순
  });

  // 페이지네이션
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentBooks = sortedBooks.slice(indexOfFirstItem, indexOfLastItem);

  const totalPages = Math.ceil(sortedBooks.length / itemsPerPage);

  const handlePageChange = (pageNumber) => setCurrentPage(pageNumber);

  const handleSearch = () => setCurrentPage(1); // 검색 시 첫 페이지로

  const handleBookClick = (bookId) => {
    navigate(`/book/${bookId}`);
  };

  return (
    <div>
      <Header />
      <div className="author-marketplace">
        <div className="title-container">
          <h1>북적거래</h1>
          <button className="upload-button">책 올리기</button>
        </div>
        <div className="filters">
          <select
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            className="filter-select"
          >
            <option value="전체">전체</option>
            <option value="중고거래">중고거래</option>
            <option value="중고교환">중고교환</option>
            <option value="무료나눔">무료나눔</option>
          </select>
          <select
            value={genre}
            onChange={(e) => setGenre(e.target.value)}
            className="filter-select"
          >
            <option value="전체">전체</option>
            <option value="소설">소설</option>
            <option value="에세이">에세이</option>
            <option value="만화">만화</option>
            <option value="전공서적">전공서적</option>
            <option value="문제집">문제집</option>
          </select>
          <select
            value={sort}
            onChange={(e) => setSort(e.target.value)}
            className="filter-select"
          >
            <option value="최신순">최신순</option>
            <option value="낮은 가격순">낮은 가격순</option>
          </select>
        </div>
        <div className="search-container">
          <input
            type="text"
            placeholder="검색하기"
            className="search-bar"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button className="search-button" onClick={handleSearch}>
            검색
          </button>
        </div>
        <div className="books-grid">
          {currentBooks.length > 0 ? (
            currentBooks.map((book) => (
              <div
                key={book.id}
                className="book-card"
                onClick={() => handleBookClick(book.id)}
              >
                <div className="book-thumbnail">
                  <img src={book.image} alt={book.title} />
                </div>
                <h3>{book.title}</h3>
                <p>{book.author}</p>
                <span
                  className={`tag ${book.tag === "무료나눔" ? "free" : "used"}`}
                >
                  {book.tag}
                </span>
              </div>
            ))
          ) : (
            <p>검색 결과가 없습니다.</p>
          )}
        </div>

        {/* 페이지네이션 */}
        {sortedBooks.length > itemsPerPage && (
          <div className="pagination">
            {[...Array(totalPages)].map((_, index) => (
              <button
                key={index}
                className={`page-button ${currentPage === index + 1 ? "active" : ""}`}
                onClick={() => handlePageChange(index + 1)}
              >
                {index + 1}
              </button>
            ))}
          </div>
        )}
      </div>
      <Footer />
    </div>
  );
};

export default Author;
