import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Author.css";
import Header from "../components/Header";
import Footer from "../components/Footer";

const books = [
  { id: 1, title: "달까지 가자 완벽해결", author: "이이에", tag: "중고거래", category: "소설" },
  { id: 2, title: "아몬드", author: "손원평", tag: "중고거래", category: "소설" },
  { id: 3, title: "오늘밤 세계에서 이 사랑이 사라진다 해도", author: "이치조 미사키", tag: "중고거래", category: "로맨스" },
  { id: 4, title: "돌이킬 수 없는 약속", author: "아카가와 쥰", tag: "중고거래", category: "미스터리" },
  { id: 5, title: "1퍼센트 부자의 법칙", author: "가나다", tag: "중고거래", category: "자기계발" },
  { id: 6, title: "바다가 들리는 영화제3", author: "마지다 소노코", tag: "중고거래", category: "소설" },
  { id: 7, title: "시와 선택", author: "한정현", tag: "중고거래", category: "시집" },
  { id: 8, title: "마지막 마음이 닿는 공중전화", author: "이수연", tag: "중고거래", category: "로맨스" },
  { id: 9, title: "독고치킨과 백예이는 학교 싶어", author: "백예인", tag: "무료나눔", category: "소설" },
  { id: 10, title: "너의 초록으로 다시", author: "나태주", tag: "중고거래", category: "시집" },
  { id: 11, title: "달의거리 고백해결", author: "이이에", tag: "중고거래", category: "소설" },
  { id: 12, title: "불완한 완벽", author: "김지연", tag: "중고거래", category: "자기계발" },
  { id: 13, title: "내 마음의 서재", author: "김훈", tag: "중고거래", category: "소설" },
  { id: 14, title: "좋은 것만 기억하라", author: "손미나", tag: "중고거래", category: "에세이" },
  { id: 15, title: "그 산이 정말 거기 있었을까", author: "박완서", tag: "중고거래", category: "소설" },
  { id: 16, title: "모든 순간이 너였다", author: "하태완", tag: "중고거래", category: "에세이" },
  { id: 17, title: "소크라테스 익스프레스", author: "에릭 와이너", tag: "중고거래", category: "자기계발" },
  { id: 18, title: "언어의 온도", author: "이기주", tag: "중고거래", category: "에세이" },
  { id: 19, title: "미드나잇 라이브러리", author: "매트 헤이그", tag: "무료나눔", category: "소설" },
  { id: 20, title: "시작의 기술", author: "개리 비숍", tag: "중고거래", category: "자기계발" },
];

const categories = ["전체", "소설", "로맨스", "미스터리", "자기계발", "시집", "에세이"];

const Author = () => {
  const [currentPage, setCurrentPage] = useState(1); // 현재 페이지 상태
  const [searchTerm, setSearchTerm] = useState(""); // 검색 상태
  const [selectedCategory, setSelectedCategory] = useState("전체"); // 선택된 카테고리 상태
  const itemsPerPage = 16; // 한 페이지에 표시할 책의 개수
  const navigate = useNavigate();

  // 검색 및 카테고리 필터링 적용
  const filteredBooks = books.filter((book) => {
    const matchesSearch = book.title.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory =
      selectedCategory === "전체" || book.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  // 현재 페이지에 해당하는 책만 가져오기
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentBooks = filteredBooks.slice(indexOfFirstItem, indexOfLastItem);

  // 전체 페이지 수 계산
  const totalPages = Math.ceil(filteredBooks.length / itemsPerPage);

  // 페이지 이동
  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  // 검색 버튼 클릭 핸들러
  const handleSearch = () => {
    setCurrentPage(1); // 검색 후 첫 페이지로 이동
  };

  // 책 클릭 시 상세 페이지로 이동
  const handleBookClick = (bookId) => {
    navigate(`/book/${bookId}`);
  };

  return (
    <div>
      <Header />
      <div className="author-marketplace">
        <header className="marketplace-header">
          <h1>북적거래</h1>
          <button className="upload-button">책 올리기</button>
          <div className="search-container">
            <input
              type="text"
              placeholder="검색하기"
              className="search-bar"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)} // 검색어 업데이트
            />
            <button className="search-button" onClick={handleSearch}>
              검색
            </button>
          </div>
          <div className="category-filter">
            {categories.map((category) => (
              <button
                key={category}
                className={`category-button ${
                  selectedCategory === category ? "active" : ""
                }`}
                onClick={() => {
                  setSelectedCategory(category);
                  setCurrentPage(1); // 카테고리 변경 시 첫 페이지로 이동
                }}
              >
                {category}
              </button>
            ))}
          </div>
        </header>
        <div className="books-grid">
          {currentBooks.length > 0 ? (
            currentBooks.map((book) => (
              <div key={book.id} className="book-card" onClick={() => handleBookClick(book.id)}>
                <div className="book-thumbnail"></div>
                <h3>{book.title}</h3>
                <p>{book.author}</p>
                <span className={`tag ${book.tag === "무료나눔" ? "free" : "used"}`}>
                  {book.tag}
                </span>
              </div>
            ))
          ) : (
            <p>검색 결과가 없습니다.</p>
          )}
        </div>

        {/* 페이지네이션 */}
        {filteredBooks.length > itemsPerPage && (
          <div className="pagination">
            {[...Array(totalPages)].map((_, index) => (
              <button
                key={index}
                className={`page-button ${currentPage === index + 1 ? "active" : ""}`}
                onClick={() => handlePageChange(index + 1)}
              >
                {index + 1}
              </button>
            ))}
          </div>
        )}
      </div>
      <Footer />
    </div>
  );
};

export default Author;
